<?phpnamespace Elementor;if ( ! defined( 'ABSPATH' ) ) {    exit; // Exit if accessed directly.}/** * Blog block * * @since 1.0.0 */class BDchomok_Author_List extends Widget_Base {    /**     * Get widget name.     *     * Retrieve oEmbed widget name.     *     * @since 1.0.0     * @access public     *     * @return string Widget name.     */    public function get_name() {        return 'Author List';    }    /**     * Get widget title.     *     * Retrieve oEmbed widget title.     *     * @since 1.0.0     * @access public     *     * @return string Widget title.     */    public function get_title() {        return __( 'Author List', 'bdchomok' );    }    /**     * Get widget icon.     *     * Retrieve oEmbed widget icon.     *     * @since 1.0.0     * @access public     *     * @return string Widget icon.     */    public function get_icon() {        return 'fa fa-file-text';    }    /**     * Get widget categories.     *     * Retrieve the list of categories the oEmbed widget belongs to.     *     * @since 1.0.0     * @access public     *     * @return array Widget categories.     */    public function get_categories() {        return [ 'bdchomok' ];    }    /**     * Register oEmbed widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     *     * @since 1.0.0     * @access protected     */    protected function _register_controls() {        $this->start_controls_section(            'author_list_sections',            [                'label' => __( 'Author List', 'bdchomok' ),                'tab' => \Elementor\Controls_Manager::TAB_CONTENT,            ]        );        $repeater = new \Elementor\Repeater();        $repeater->add_control(            'list_title', [                'label' => __( 'Title', 'bdchomok' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => __( 'হুমায়ূন আহমেদ' , 'bdchomok' ),                'label_block' => true,            ]        );        $repeater->add_control(            'list_content', [                'label' => __( 'Content', 'bdchomok' ),                'type' => \Elementor\Controls_Manager::MEDIA,                'default' => [                    'url' => \Elementor\Utils::get_placeholder_image_src(),                ]            ]        );        $repeater->add_control(            'list_url',            [                'label' => __( 'URL', 'bdchomok' ),                'type' => \Elementor\Controls_Manager::URL,                'placeholder' => __( 'https://your-link.com', 'bdchomok' ),                'show_external' => true,                'default' => [                    'url' => '#',                    'is_external' => true,                    'nofollow' => true,                ],            ]        );        $this->add_control(            'list',            [                'label' => __( 'List', 'bdchomok' ),                'type' => \Elementor\Controls_Manager::REPEATER,                'fields' => $repeater->get_controls(),                'default' => [                    [                        'list_title' => __( 'হুমায়ূন আহমেদ', 'bdchomok' )                    ]                ],                'title_field' => '{{{ list_title }}}',            ]        );        $this->end_controls_section();    }    /**     * Render oEmbed widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     *     * @since 1.0.0     * @access protected     */    protected function render() {        $settings = $this->get_settings_for_display();        ?>        <div class="category-filter-wrap mb-3 col-12 cat-tab mb-5">            <div class="d-block text-center">                <a class="text-uppercase position-relative">                    <span><?php echo esc_html( 'Author List' ); ?></span>                    <i class="icofont-read-book" style="background-color: #fff;"></i>                </a>                <span class="seperater"></span>            </div>        </div>        <!-- ul.author-meta -->        <ul class="list-inline author-meta author-slider mt-3">            <?php            if ( $settings['list'] ) {                foreach (  $settings['list'] as $item ) {                    ?>                    <li class="list-inline-item text-center">                        <a href="<?php echo esc_url( $item['list_url']['url'] ); ?>">                            <img class="author-pic" src="<?php echo esc_url( $item['list_content']['url'] ); ?>" />                            <p>                                <?php echo $item['list_title']; ?>                            </p>                        </a>                    </li>                    <?php                }            }            ?>        </ul>        <!-- ul.author-meta -->        <?php    }    protected function _content_template() {        ?>        <# if ( settings.list.length ) { #>        <ul class="list-inline author-meta author-slider mt-3">            <# _.each( settings.list, function( item ) { #>            <li class="list-inline-item text-center">                <a href="{{ item.list_url.url }}">                    <img class="author-pic" src="{{ item.list_content.url }}" />                    <p>                        {{ item.list_title }}                    </p>                </a>            </li>            <# }); #>        </ul>        <# } #>        <?php    }}Plugin::instance()->widgets_manager->register_widget_type( new BDchomok_Author_List() );